namespace WebSite
{
    public partial class OperateDetail : BasePage
    {
        private OperateService operateService=new OperateService();
		
		#region ---界面处理方法---

       	protected bool ShowPageDetail()
        {
			if (PageContext.Action == ActionType.Add)
			{
				return false;
			}

            Operate entity =operateService.GetDomain(CurrentId);
			
            if (entity == null) return false;

			SetControlValues(entity);
			
			return true;
        }
        protected void Page_Load(object sender, EventArgs e)
        {
            if (!IsPostBack && !IsAjaxPost)
            {
                ShowPageDetail();
            }
        }
		#endregion
		
		#region ---操作处理方法---
		protected Operate GetDomainObject()
		{
            Operate  operate = operateService.GetDomain(CurrentId);

            if (operate == null)
            {
                operate = new Operate();
				operate.ID=CurrentId;
            }

			GetControlValues(ref operate);	
			
            return operate;
		}

		#endregion
    }
}
