namespace WebSite
{
    public partial class FamilyMemberDetail : BasePage
    {
        private FamilyMemberService familyMemberService=new FamilyMemberService();
		
		#region ---界面处理方法---

       	protected bool ShowPageDetail()
        {
			if (PageContext.Action == ActionType.Add)
			{
				dtpCreateTime.Text=DateTime.Now.ToShortDateString();
				return false;
			}

            FamilyMember entity =familyMemberService.GetDomain(CurrentId);
			
            if (entity == null) return false;

			SetControlValues(entity);
			
			return true;
        }
        protected void Page_Load(object sender, EventArgs e)
        {
            if (!IsPostBack)
            {
                ShowPageDetail();
            }
        }
		#endregion
		
		#region ---操作处理方法---
		protected FamilyMember GetDomainObject()
		{
            FamilyMember  familyMember = familyMemberService.GetDomain(CurrentId);

            if (familyMember == null)
            {
                familyMember = new FamilyMember();
				familyMember.ID=CurrentId;
            }

			GetControlValues(ref familyMember);	
			
            return familyMember;
		}

		#endregion
    }
}
