namespace WebSite
{
    public partial class ResumeDetail : BasePage
    {
        private ResumeService resumeService=new ResumeService();
		
		#region ---界面处理方法---

       	protected bool ShowPageDetail()
        {
			if (PageContext.Action == ActionType.Add)
			{
				dtpCreateTime.Text=DateTime.Now.ToShortDateString();
				return false;
			}

            Resume entity =resumeService.GetDomain(CurrentId);
			
            if (entity == null) return false;

			SetControlValues(entity);
			
			return true;
        }
        protected void Page_Load(object sender, EventArgs e)
        {
            if (!IsPostBack)
            {
                ShowPageDetail();
            }
        }
		#endregion
		
		#region ---操作处理方法---
		protected Resume GetDomainObject()
		{
            Resume  resume = resumeService.GetDomain(CurrentId);

            if (resume == null)
            {
                resume = new Resume();
				resume.ID=CurrentId;
            }

			GetControlValues(ref resume);	
			
            return resume;
		}

		#endregion
    }
}
